function createBtn(name, id, link) {
    let result = ""
    result += "\`\`\`button\n"
    result += `name ${name}\n`
    result += "type link\n"
    result += `action ${link}\n`
    result += `class button-${id}\n`
    result += "hidden true\n"
    result += "\`\`\`\n"
    result += `^button-${id}\n`
    return result
}

