}

const modalForm = app.plugins.plugins.modalforms.api;
const result = await modalForm.openForm('button-form');
let links = result.asString("{{Links}}")
let returnValue = ""

if (links !== "{{Links}}") {
	links = links.split(",")
	for (let i = 0; i < links.length; i++) {
		let index = linkInfo.links.findIndex(link => links[i].includes(link))
		if (index == -1)
			returnValue += createBtn(`Button ${i}`, `${i}`, links[i])
		else
			returnValue += createBtn(linkInfo.buttons[index], linkInfo.names[index], links[i])
	}
}

if (links !== "{{Links}}") {
	for (let i = 0; i < links.length; i++) {
		let index = linkInfo.links.findIndex(link => links[i].includes(link))
		let btnId = ""
		if (index == -1)
			btnId = `${i}`
		else
			btnId = linkInfo.names[index]

		if (i % 2 == 0)
			returnValue += `\n\n\`button-${btnId}\``
		else
			returnValue += ` \`button-${btnId}\``
	}
}

return returnValue
